const fs=require("fs");module.exports.writeLog=function({text:t,type:e}){fs.existsSync("./logs")||fs.mkdirSync("./logs",484);var s=new Date,n=`${s.getFullYear()}-${(s.getMonth()+1).toString().padStart(2,"00")}-${s.getDate().toString().padStart(2,"00")}.log`;const o=fs.openSync("./logs/"+n,"a");fs.writeSync(o,`[${s}]   ${e}   '${t}'\n`),fs.closeSync(o)};